import React, { Component } from 'react';
import sampleTasks from './tasks1.json';
import logo from './logo.svg';
import './App.css';
//import { Container,Row,Col,ButtonGroup,Button,ListGroup } from 'react-bootstrap';
//import ViewPanel from './ViewPanel.js'

import classnames from 'classnames';
import Prism from 'prismjs';
import "./prism.css";
import { HtmlRenderer, Parser } from 'commonmark';
import { 
    Container,Row,Col,Jumbotron,
    TabContent, TabPane,Nav, NavItem, NavLink,
    Dropdown, DropdownItem, DropdownToggle, DropdownMenu, 
    Alert,Progress,
    ButtonGroup,Button,
    Form, FormGroup, Label, Input,
} from 'reactstrap';
import 'whatwg-fetch';
import config from './config.js';
import moment from 'moment';

class App extends Component {
  constructor(props) {
    super(props);

    this.toggle = this.toggle.bind(this);
    this.state={
      tasks:sampleTasks,
      curTask:0,
      mode:"view",
      activeTab: 0,
      //tasks:[],
      loadingTasks: false,
      addForm:true,
      error:'',
      success:false
    }

  }

  componentDidMount() {
    Prism.highlightAll();

    this.setState({ loadingTasks:true});

    // let url = config.API_URL+'tasks';
    // fetch(url,{}).then(res => res.json())
    // .then(json => {
    //   console.log('enter',json);
    //   this.setState({
    //     tasks: json.tasks,
    //     loadingTasks:false
    //   });
    // }, error =>{
    //   this.setState({ loadingTasks:false});
    //   console.log('err',error);
    // });
  }

  toggle(tab) {
    if (this.state.activeTab !== tab) {
      this.setState({
        activeTab: tab
      });
    }
  }

  showAddForm(){
    this.setState({
      addForm: true
    });
  }

  render() {
    console.log('enter',this.state.activeTab);
    //let curTask = this.state.tasks[this.state.activeTab];
    //let curIns = this.state.tasks[this.state.activeTab].instructions;
    let that=this;
    let listItems=this.state.tasks.map(function(item,i){
      return(
        <NavItem key={i}>
          <NavLink
            className={classnames({ active: that.state.activeTab === i })}
            onClick={() => { that.toggle(i); }}
          >
            {item["task-name"]}
          </NavLink>
        </NavItem>
      );
    })

    let parser = new Parser()
    let renderer = new HtmlRenderer()
    //let ins = renderer.render(parser.parse(curTask.instructions));

    let listTabs=this.state.tasks.map(function(item,i){
      let ins = renderer.render(parser.parse(item.instructions));
      let active = '';
      if(i===that.state.activeTab){
        active = 'active';
      }else{
        active = '';
      }
      let dueDate = moment(item.due).format('dddd, MMMM Do YYYY, hh:mm A');

      return(
        <TabContent vertical activeTab="{this.state.activeTab}">
        <TabPane tabId="{i}" className={active}>
          <Row>
            <Col sm="12">
            <pre>
            <code >
            {/* {curTask.instructions} */}
            <div dangerouslySetInnerHTML={{__html: ins}}></div>
            </code>
            </pre>
            Status: {item.status}, Due: {dueDate}

              {/* <h4>{curTask.instructions}</h4> */}
            </Col>
            <Col sm="12">
              <ButtonGroup>
                <Button color="primary" onClick={() => { that.showAddForm(); }}>Add</Button>
                <Button color="primary">Update</Button>
                <Button color="primary">Delete</Button>
              </ButtonGroup>
            </Col>
          </Row>
        </TabPane>
        </TabContent>
    );
  })

    return(
        <Container>
          <Container><h1 className="text-center">Title here</h1></Container>

          { this.state.addForm ?
          <div>
            <Row>
            <Col sm="6">
            <Form onSubmit={this.handleSubmit}>
              <FormGroup row>
                <Label for="taskName" sm={3}>Name</Label>
                <Col sm={5}>
                  <Input value={this.state.taskName} onChange={this.handleChange} type="text" name="taskName" id="taskName" placeholder="Enter task name" />
                </Col>
                <Col sm="4">
                <Input value={this.state.status} onChange={this.handleChange} type="select" name="status" id="status">
                  <option value="Open">Open</option>
                  <option value="Closed">Closed</option>
                </Input>
                </Col>
              </FormGroup>
              <FormGroup row>
                <Label for="dueDate" sm={3}>Due</Label>
                <Col sm={5}>
                  <Input value={this.state.dueDate} onChange={this.handleChange} type="date" name="dueDate" id="dueDate" placeholder="Enter due date" />
                </Col>
                <Col sm="4">
                  <Input value={this.state.dueTime} onChange={this.handleChange}  type="time" name="dueTime" id="dueTime" placeholder="Enter due time" />
                </Col>                
              </FormGroup>
              {/* <FormGroup tag="fieldset" row>
                <Label for="status" sm={3}>Status</Label>
                <Col sm={9}>
                  <Input value={this.state.status} onChange={this.handleChange} type="select" name="status" id="status">
                  <option value="Open">Open</option>
                  <option value="Closed">Closed</option>
                </Input>
              </Col>
              </FormGroup> */}
              <FormGroup row>
                {/* <Label for="instructions" sm={3}>Instructions</Label> */}
                <Col sm={12}>
                  <Input value={this.state.instructions} onChange={this.handleChange} type="textarea" name="instructions" id="instructions" />
                </Col>
              </FormGroup>
              {this.state.error !== '' && <Alert color="warning">Error: {this.state.error}</Alert>}
              {this.state.success && <Alert color="success">Successfully Added</Alert>}  
              <FormGroup check row>
                <Col sm={{ size: 12}}>
                  <Button className="float-right">Submit</Button>
                  <Button color="secondary" onClick={this.openAddModal}>Close</Button>
                </Col>
              </FormGroup>
            </Form>
            </Col>
            <Col>
              <div>
                {this.state.ins}
              </div>
            </Col>
            </Row>
          </div>
          :

          <div>
          {/* { this.state.loadingTasks && 
            <Progress animated color="warning" value="100" />
          }

          { listItems && listItems.length == 0 &&
            <Alert color="warning" className="text-center">No results found</Alert>
          } */}

          { listItems && listItems.length > 0 &&
            <Row>
            <Col sm="3">
              <Nav vertical pills>
                {listItems}
              </Nav>
            </Col>
            <Col sm="9">
              <Jumbotron>
                {listTabs}
              </Jumbotron>
            </Col>
            </Row>}
            </div>
          }
        </Container>


    );
  }

}

export default App;
