import React, { Component } from 'react';
//import tasks from './tasks1.json';
import './App.css';
import Prism from 'prismjs';
import "./prism.css";
import { HtmlRenderer, Parser } from 'commonmark';
import { 
  Container,Row,Col,Jumbotron,
  TabContent, TabPane,Nav, NavItem, NavLink,
  Dropdown, DropdownItem, DropdownToggle, DropdownMenu, 
  Alert,Progress,
  ButtonGroup,Button,
  Form, FormGroup, Label, Input,
} from 'reactstrap';

class ActionPanel extends Component {
  constructor(props) {
    super();
  }
  componentDidMount() {
    Prism.highlightAll();
  }

  openEditModal (data) {
    let dueDate = moment(data.due).utc().format('YYYY-MM-DD');
    let dueTime = moment(data.due).utc().format('HH:mm');

    this.setState({
       addForm: !this.state.addForm,
       editMode:true,
       taskName:data["task-name"],
       status:data.status,
       dueDate:dueDate,
       dueTime:dueTime,
       instructions:data.instructions
    });
  }  

  render() {
    let contenta="";
    contenta = this.props.instructions;
    let state = this.props.state;
console.log('enter',contenta);
    let parser = new Parser()
    let renderer = new HtmlRenderer()
    let ins = renderer.render(parser.parse(contenta));

    return <div>
            <Row>
            <Col sm="6">
            <Form onSubmit={this.handleSubmit}>
              <FormGroup row>
                <Label for="taskName" sm={3}>Name</Label>
                <Col sm={5}>
                  <Input disabled={state.editMode} value={state.taskName} onChange={this.handleChange} type="text" name="taskName" id="taskName" placeholder="Enter task name" required/>
                </Col>
                <Col sm="4">
                <Input value={this.state.status} onChange={this.handleChange} type="select" name="status" id="status">
                  <option value="Open">Open</option>
                  <option value="Closed">Closed</option>
                </Input>
                </Col>
              </FormGroup>
              <FormGroup row>
                <Label for="dueDate" sm={3}>Due</Label>
                <Col sm={5}>
                  <Input value={state.dueDate} onChange={this.handleChange} type="date" name="dueDate" id="dueDate" placeholder="Enter due date" required />
                </Col>
                <Col sm="4">
                  <Input value={state.dueTime} onChange={this.handleChange}  type="time" name="dueTime" id="dueTime" placeholder="Enter due time" required />
                </Col>                
              </FormGroup>
              {/* <FormGroup tag="fieldset" row>
                <Label for="status" sm={3}>Status</Label>
                <Col sm={9}>
                  <Input value={this.state.status} onChange={this.handleChange} type="select" name="status" id="status">
                  <option value="Open">Open</option>
                  <option value="Closed">Closed</option>
                </Input>
              </Col>
              </FormGroup> */}
              <FormGroup row>
                {/* <Label for="instructions" sm={3}>Instructions</Label> */}
                <Col sm={12}>
                  <Input rows={10} value={state.instructions} onChange={this.handleChange} type="textarea" name="instructions" id="instructions" required />
                </Col>
              </FormGroup>
              {state.error !== '' && <Alert color="warning">Error: {state.error}</Alert>}
              {/* {this.state.success && <Alert color="success">Successfully Added</Alert>}   */}
              <FormGroup check row>
                <ButtonGroup>
                  <Button color="primary" >Commit</Button>
                  <Button color="primary" onClick={this.showAddForm}>Cancel</Button>
                </ButtonGroup>
              </FormGroup>
            </Form>
            </Col>
            <Col sm="6">
              <div>
                <pre>
                  <code className="language-markup">
                  <div dangerouslySetInnerHTML={{__html: ins}}></div>
                  </code>
                </pre>
              </div>
            </Col>
            <Col sm="12">
              <ButtonGroup>
              <Button color="primary" onClick={() => { this.showAddForm(); }}>Add</Button>
              <Button color="primary" onClick={this.openEditModal.bind(this, item)}>Update</Button>
              <Button color="primary" onClick={this.handleDelete.bind(this, item)}>Delete</Button>
              </ButtonGroup>
            </Col>
            </Row>

          </div>

  }
}
export default ActionPanel;
