import React, { Component } from 'react';
//import tasks from './tasks1.json';
import './App.css';
import Prism from 'prismjs';
import "./prism.css";
import { HtmlRenderer, Parser } from 'commonmark';
import { 
  Container,Row,Col,Jumbotron,
  TabContent, TabPane,Nav, NavItem, NavLink,
  Dropdown, DropdownItem, DropdownToggle, DropdownMenu, 
  Alert,Progress,
  ButtonGroup,Button,
  Form, FormGroup, Label, Input,
} from 'reactstrap';
import moment from 'moment';

class ViewPanel extends Component {
  constructor(props) {
    super();
    console.log('props',props);
  }

  componentDidMount() {
    Prism.highlightAll();
  }

  openEditModal (data) {
    let dueDate = moment(data.due).utc().format('YYYY-MM-DD');
    let dueTime = moment(data.due).utc().format('HH:mm');

    this.setState({
       addForm: !this.state.addForm,
       editMode:true,
       taskName:data["task-name"],
       status:data.status,
       dueDate:dueDate,
       dueTime:dueTime,
       instructions:data.instructions
    });
  }  

  render() {
    let contenta="";
    contenta = this.props.tasks[this.props.curTask].instructions;
    let task = this.props.tasks[this.props.curTask];
    console.log('enter',contenta);
    let parser = new Parser()
    let renderer = new HtmlRenderer()
    let ins = renderer.render(parser.parse(contenta));
    return (
      <div>
      <Col sm="12">
        <pre><code><div dangerouslySetInnerHTML={{__html: ins}}></div>
         </code></pre>     
      </Col>
      <Col sm="12">
        <ButtonGroup>
          <Button color="primary" onClick={() => { this.showAddForm(); }}>Add</Button>
          <Button color="primary" onClick={this.openEditModal.bind(this, task)}>Update</Button>
          <Button color="primary" onClick={this.handleDelete.bind(this, task)}>Delete</Button>
        </ButtonGroup>
      </Col>
      </div>
      );     
  }
}
export default ViewPanel;
