import React, { Component } from 'react';
import sampleTasks from './tasks1.json';
import logo from './logo.svg';
import './App.css';
import { Container,Row,Col,ButtonGroup,Button,ListGroup,Tabs,Tab } from 'react-bootstrap';
import ViewPanel from './ViewPanel.js'


class App extends Component {
  constructor(props) {
    super(props);
    this.state={tasks:sampleTasks,curTask:0,mode:"view"}
  }
  taskClick(i){
    let curIns =this.state.tasks[this.state.curTask].instructions;
    this.setState(
      {
        curTask:i
      });


  }
  render() {
    let curIns =this.state.tasks[this.state.curTask].instructions;
    let that=this;
    let listItems=this.state.tasks.map(function(item,i){
      if(i===that.state.curTask){
        return(
        <ListGroup.Item
        key={i}
        active
        action
        onClick={that.taskClick.bind(that,i)}
        >
        {item["task-name"]}
        </ListGroup.Item>
      );
    }else{
      return(
      <ListGroup.Item key={i} action onClick={that.taskClick.bind(that,i)}>
      {item["task-name"]}
      </ListGroup.Item>
    );
    }
    })
    return(
      <Tabs placement='right' >
    <Tab ref="target1" eventKey={1} title="Tab 1">

    </Tab>
    <Tab target="target1" placement="bottom" eventKey={2} title="Tab 2">
    Tab 2 content
    </Tab>
    </Tabs>


    );
  }

}

export default App;
