import React, { Component } from 'react';
import sampleTasks from './tasks1.json';
import logo from './logo.svg';
import './App.css';
import { Container,Row,Col,ButtonGroup,Button,ListGroup } from 'react-bootstrap';
import ViewPanel from './ViewPanel.js'
import classnames from 'classnames';


import { TabContent, TabPane,Nav, NavItem, Dropdown, DropdownItem, DropdownToggle, DropdownMenu, NavLink } from 'reactstrap';


class App extends Component {
  constructor(props) {
    super(props);

    this.taskClick = this.toggle.bind(this);

    this.state={tasks:sampleTasks,curTask:0,mode:"view"}
  }
  taskClick(i){
    let curIns =this.state.tasks[this.state.curTask].instructions;
    this.setState(
      {
        curTask:i
      });
  }

  render() {
    let curIns =this.state.tasks[this.state.curTask].instructions;
    let that=this;
    let listItems=this.state.tasks.map(function(item,i){
        return(
          <NavItem >
            <NavLink
              key={i}
              className={classnames({ active: this.state.curTask === '{i}' })}
              onClick={() => { this.taskClick('{i}'); }}
            >
              {item["task-name"]}
            </NavLink>
          </NavItem>
        // <ListGroup.Item
        // key={i}
        // active
        // action
        // onClick={that.taskClick.bind(that,i)}
        // >
        // {item["task-name"]}
        // </ListGroup.Item>
      );
    })

    let listTabs = this.state.tasks.map(function(item,i){
      return(
          <TabPane tabId="{i}">
          <Row>
            <Col sm="12">
              <h4>{item["instructions"]}</h4>
            </Col>
          </Row>
        </TabPane>
      );
    });
    return(

      <div>
        <Row>
        <Col sm="3">
          <Nav vertical tabs>
            {listItems}
          </Nav>
        </Col>
        <Col sm="9">
          <TabContent vertical activeTab={this.state.curTask}>
          {listTabs}
          </TabContent>
        </Col>
        </Row>
    </div>

      // <Container>
      // <Row>
      // <p>May be something </p>
      // <tr><td>{listItems}</td><td> </td></tr>
      // <tr><td></td><td><ViewPanel tasks={this.state.sampleTasks} curTask={this.state.curTask} /></td></tr>
      // </Row>

      // </Container>

    );
  }

}

export default App;
