import React, { Component } from 'react';
import sampleTasks from './tasks1.json';
import logo from './logo.svg';
import './App.css';
import { Container,Row,Col,ButtonGroup,Button,ListGroup } from 'react-bootstrap';
import ViewPanel from './ViewPanel.js'

import { Nav, NavItem, Dropdown, DropdownItem, DropdownToggle, DropdownMenu, NavLink } from 'reactstrap';


class App extends Component {
  constructor(props) {
    super(props);
    this.state={tasks:sampleTasks,curTask:0,mode:"view"}
  }
  taskClick(i){
    let curIns =this.state.tasks[this.state.curTask].instructions;
    this.setState(
      {
        curTask:i
      });


  }
  render() {
    let curIns =this.state.tasks[this.state.curTask].instructions;
    let that=this;
    let listItems=this.state.tasks.map(function(item,i){
      if(i===that.state.curTask){
        return(
        <ListGroup.Item
        key={i}
        active
        action
        onClick={that.taskClick.bind(that,i)}
        >
        {item["task-name"]}
        </ListGroup.Item>
      );
    }else{
      return(
      <ListGroup.Item key={i} action onClick={that.taskClick.bind(that,i)}>
      {item["task-name"]}
      </ListGroup.Item>
    );
    }
    })
    return(
      <div>
      <Nav vertical tabs>
        <NavItem>
          <NavLink href="#" active>Link</NavLink>
        </NavItem>
        <Dropdown nav isOpen={this.state.dropdownOpen} toggle={this.toggle}>
          <DropdownToggle nav caret>
            Dropdown
          </DropdownToggle>
          <DropdownMenu>
            <DropdownItem header>Header</DropdownItem>
            <DropdownItem disabled>Action</DropdownItem>
            <DropdownItem>Another Action</DropdownItem>
            <DropdownItem divider />
            <DropdownItem>Another Action</DropdownItem>
          </DropdownMenu>
        </Dropdown>
        <NavItem>
          <NavLink href="#">Link</NavLink>
        </NavItem>
        <NavItem>
          <NavLink href="#">Another Link</NavLink>
        </NavItem>
        <NavItem>
          <NavLink disabled href="#">Disabled Link</NavLink>
        </NavItem>
      </Nav>
    </div>


    );
  }

}

export default App;
