import React, { Component } from 'react';
import sampleTasks from './tasks1.json';
import logo from './logo.svg';
import './App.css';
import { Container,Row,Col,ButtonGroup,Button,ListGroup } from 'react-bootstrap';
import ViewPanel from './ViewPanel.js'
import classnames from 'classnames';


import { TabContent, TabPane,Nav, NavItem, Dropdown, DropdownItem, DropdownToggle, DropdownMenu, NavLink } from 'reactstrap';


class App extends Component {
  constructor(props) {
    super(props);
    this.state={tasks:sampleTasks,curTask:0,mode:"view"}
  }
  taskClick(i){
    let curIns =this.state.tasks[this.state.curTask].instructions;
    this.setState(
      {
        curTask:i
      });


  }
  toggle(tab) {
    if (this.state.activeTab !== tab) {
      this.setState({
        activeTab: tab
      });
    }
  }

  render() {
    let curIns =this.state.tasks[this.state.curTask].instructions;
    let that=this;
    let listItems=this.state.tasks.map(function(item,i){
      if(i===that.state.curTask){
        return(
        <ListGroup.Item
        key={i}
        active
        action
        onClick={that.taskClick.bind(that,i)}
        >
        {item["task-name"]}
        </ListGroup.Item>
      );
    }else{
      return(
      <ListGroup.Item key={i} action onClick={that.taskClick.bind(that,i)}>
      {item["task-name"]}
      </ListGroup.Item>
    );
    }
    })
    return(

      <div>
        <Col sm="3">
        <Nav vertical tabs>
          <NavItem>
            <NavLink
              className={classnames({ active: this.state.activeTab === '1' })}
              onClick={() => { this.toggle('1'); }}
            >
              Tab1
            </NavLink>
          </NavItem>
          <NavItem>
            <NavLink
              className={classnames({ active: this.state.activeTab === '2' })}
              onClick={() => { this.toggle('2'); }}
            >
              Moar Tabs
            </NavLink>
          </NavItem>
        </Nav>
        </Col>
        <Col>
          <TabContent vertical activeTab={this.state.activeTab}>
          <TabPane tabId="1">
            <Row>
              <Col sm="12">
                <h4>Tab 1 Contents</h4>
              </Col>
            </Row>
          </TabPane>
          <TabPane tabId="2">
            <Row>
              <Col sm="12">TEst
              </Col>
            </Row>
          </TabPane>
        </TabContent>
        </Col>
    </div>

      // <Container>
      // <Row>
      // <p>May be something </p>
      // <tr><td>{listItems}</td><td> </td></tr>
      // <tr><td></td><td><ViewPanel tasks={this.state.sampleTasks} curTask={this.state.curTask} /></td></tr>
      // </Row>

      // </Container>

    );
  }

}

export default App;
