import React, { Component } from 'react';
import sampleTasks from './tasks1.json';
import logo from './logo.svg';
import './App.css';
import { Container,Row,Col,ButtonGroup,Button,ListGroup } from 'react-bootstrap';
import ViewPanel from './ViewPanel.js'

import { TabProvider, Tab, TabPanel, TabList } from 'react-web-tabs';


class App extends Component {
  constructor(props) {
    super(props);
    this.state={tasks:sampleTasks,curTask:0,mode:"view"}
  }
  taskClick(i){
    let curIns =this.state.tasks[this.state.curTask].instructions;
    this.setState(
      {
        curTask:i
      });


  }
  render() {
    let curIns =this.state.tasks[this.state.curTask].instructions;
    let that=this;
    let listItems=this.state.tasks.map(function(item,i){
      if(i===that.state.curTask){
        return(
        <ListGroup.Item
        key={i}
        active
        action
        onClick={that.taskClick.bind(that,i)}
        >
        {item["task-name"]}
        </ListGroup.Item>
      );
    }else{
      return(
      <ListGroup.Item key={i} action onClick={that.taskClick.bind(that,i)}>
      {item["task-name"]}
      </ListGroup.Item>
    );
    }
    })
    return(
      <TabProvider defaultTab="one">
        <section className="my-tabs">
          <TabList className="my-tablist">
            <Tab tabFor="one">Tab 1</Tab>
            <span className="divider">•</span>
            <Tab tabFor="two">Tab 2</Tab>
            <span className="divider">•</span>
            <Tab tabFor="three" className="my-tab">Tab 3</Tab>
          </TabList>
          <div className="wrapper">
            <TabPanel tabId="one">
              <p>Tab 1 content</p>
            </TabPanel>
            <TabPanel tabId="two">
              <p>Tab 2 content</p>
            </TabPanel>
            <TabPanel tabId="three">
              <p>Tab 3 content</p>
            </TabPanel>
          </div>
        </section>
      </TabProvider>


    );
  }

}

export default App;
